mapper.select:
SELECT DISTINCT sysResource.id, sysResource.tenant_id, sysResource.parent_id, sysResource.ancestors, sysResource.resource_name, sysResource.resource_key, sysResource.resource_type, sysResource.resource_icon, sysResource.resource_path, sysResource.resource_url, sysResource.resource_cache, sysResource.resource_show, sysResource.resource_level, sysResource.resource_page_name, sysResource.resource_status FROM t_sys_user sysUser INNER JOIN t_sys_user_role userRole ON sysUser.id = userRole.user_id AND userRole.del_flag = 0 INNER JOIN t_sys_role_resource roleResource ON userRole.role_id = roleResource.role_id AND roleResource.del_flag = 0 INNER JOIN t_sys_resource sysResource ON roleResource.resource_id = sysResource.id AND sysResource.del_flag = 0 WHERE sysUser.del_flag = 0
AND sysUser.id = #{userResource.userId}
and sysResource.resource_type in
#{resourceType}
ORDER BY resource_level
