mapper.select:
SELECT DISTINCT sysUser.id AS id, sysUser.tenant_id AS tenantId, sysUser.account AS account, sysUser.nickname AS nickname, sysUser.real_name AS realName, sysUser.gender AS gender, sysUser.email AS email, sysUser.mobile AS mobile, sysUser.password AS password, sysUser.status AS status, sysUser.avatar AS avatar, sysUser.country AS country, sysUser.province AS province, provinceDistrict.name AS provinceName, sysUser.city AS city, cityDistrict.name AS cityName, sysUser.area AS area, sysUser.street AS street, sysUser.comments AS comments, sysUser.create_time AS createTime, sysUser.creator AS creator, sysUser.update_time AS updateTime, sysUser.operator AS operator, GROUP_CONCAT(DISTINCT sysRole.id) AS roleIds, GROUP_CONCAT(DISTINCT sysRole.role_key) AS roleKeys, GROUP_CONCAT(DISTINCT sysRole.role_name) AS roleNames, GROUP_CONCAT(DISTINCT sysRole.data_permission_type) AS dataPermissionTypes, GROUP_CONCAT(DISTINCT dataPermission.organization_id) AS organizationIds, sysOrganization.id AS organizationId, sysOrganization.organization_name AS organizationName FROM t_sys_user sysUser INNER JOIN t_sys_user_role userRole ON sysUser.id = userRole.user_id AND userRole.del_flag = 0 LEFT JOIN t_sys_role sysRole ON userRole.role_id = sysRole.id AND sysRole.del_flag = 0 LEFT JOIN t_sys_district provinceDistrict ON sysUser.province = provinceDistrict.code LEFT JOIN t_sys_district cityDistrict ON sysUser.city = cityDistrict.code LEFT JOIN t_sys_data_permission_user dataPermission ON sysUser.id = dataPermission.user_id AND dataPermission.del_flag = 0 INNER JOIN t_sys_organization_user organizationUser ON sysUser.id = organizationUser.user_id AND organizationUser.del_flag = 0 INNER JOIN t_sys_organization sysOrganization ON organizationUser.organization_id = sysOrganization.id AND sysOrganization.del_flag = 0 WHERE sysUser.del_flag = 0
AND sysUser.id = #{user.id}
AND sysUser.account = #{user.account}
AND sysUser.real_name = #{user.realName}
AND sysUser.mobile = #{user.mobile}
AND sysUser.email = #{user.email}
GROUP BY sysUser.id

SELECT

FROM t_sys_user
WHERE del_flag = 0

	AND id != #{user.id}



		OR account = #{user.account} OR nickname = #{user.account} OR mobile = #{user.account} OR email = #{user.account}


		OR account = #{user.nickname} OR nickname = #{user.nickname} OR mobile = #{user.nickname} OR email = #{user.nickname}


		OR account = #{user.mobile} OR nickname = #{user.mobile} OR mobile = #{user.mobile} OR email = #{user.mobile}


		OR account = #{user.email} OR nickname = #{user.email} OR mobile = #{user.email} OR email = #{user.email}


ORDER BY id DESC

SELECT DISTINCT sysUser.id AS id, sysUser.tenant_id AS tenantId, sysUser.account AS account, sysUser.nickname AS nickname, sysUser.real_name AS realName, sysUser.gender AS gender, sysUser.email AS email, sysUser.mobile AS mobile, sysUser.status AS status, sysUser.avatar AS avatar, sysUser.country AS country, sysUser.province AS province, sysUser.city AS city, sysUser.area AS area, sysUser.street AS street, sysUser.comments AS comments, sysUser.create_time AS createTime, sysUser.creator AS creator, sysUser.update_time AS updateTime, sysUser.operator AS operator, GROUP_CONCAT(DISTINCT sysRole.id) AS roleIds, GROUP_CONCAT(DISTINCT sysRole.role_key) AS roleKey, GROUP_CONCAT(DISTINCT sysRole.role_name) AS roleName, sysOrganization.id AS organizationId, sysOrganization.organization_name AS organizationName FROM t_sys_user sysUser INNER JOIN t_sys_user_role userRole ON sysUser.id = userRole.user_id AND userRole.del_flag = 0 LEFT JOIN t_sys_role sysRole ON userRole.role_id = sysRole.id AND sysRole.del_flag = 0 INNER JOIN t_sys_organization_user organizationUser ON sysUser.id = organizationUser.user_id AND organizationUser.del_flag = 0 INNER JOIN t_sys_organization sysOrganization ON organizationUser.organization_id = sysOrganization.id AND sysOrganization.del_flag = 0 WHERE sysUser.del_flag = 0
AND sysUser.id = #{user.id}
and sysUser.id in
#{userId}
AND sysUser.account = #{user.account}
AND sysUser.nickname = #{user.nickname}
AND sysUser.real_name = #{user.realName}
AND sysUser.mobile = #{user.mobile}
AND sysUser.email = #{user.email}
AND sysUser.status = #{user.status}
AND sysUser.id IN (SELECT sysUser.id FROM t_sys_user sysUser INNER JOIN t_sys_user_role userRole ON sysUser.id = userRole.user_id AND userRole.del_flag = 0 AND userRole.role_id = #{user.roleId})
AND sysRole.role_key = #{user.roleKey}
AND sysOrganization.id = #{user.organizationId}
and sysOrganization.id in
#{organizationId}
AND DATE_FORMAT(sysUser.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{user.beginDateTime},'%Y-%m-%d')
AND DATE_FORMAT(sysUser.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{user.endDateTime},'%Y-%m-%d')
GROUP BY sysUser.id ORDER BY sysUser.create_time DESC
